        public static void FillDataSet(ref DataSet ds, string connectionString, CommandType commandType, string commandText, params IDataParameter[] commandParameters)
        {
            //create & open a connection, and dispose of it after we are done.
            using (SqlConnection cn = new SqlConnection(connectionString))
