        /// Execute a stored procedure via a command (that returns a resultset) against the database specified in 
        /// the connection string using the provided parameter values.  This method will query the database to discover the parameters for the 
        /// stored procedure (the first time each stored procedure is called), and assign the values based on parameter order.
        /// </summary>
        /// <param name="dt">the DataTable to be filled</param>
        /// <param name="transaction">a valid IDbTransaction</param>
        /// <param name="spName">the name of the stored procedure</param>
        /// <param name="ac">callback object containing a delegate</param>
        /// <param name="state">state object supplied by the client</param>
        /// <param name="parameterValues">an array of objects to be assigned as the input values of the stored procedure</param>
        /// <returns>asynchronous result object</returns>
        public static IAsyncResult BeginFillDataTable
            (ref DataTable dt, IDbTransaction transaction, string spName, AsyncCallback ac, Object state, params object[] parameterValues)
        {
            FillDataTableDelegate9 d = new FillDataTableDelegate9(FillDataTable);
            IAsyncResult result = d.BeginInvoke(ref dt, transaction, spName, parameterValues, ac, state);
            return result;
        }

        /// <summary>
        /// Receive the resulting table at the end of an asynchronous call.
        /// </summary>
        /// <param name="dt">the DataTable to be filled</param>
        /// <param name="result">asynchronous result object</param>
        public static void EndFillDataTable(ref DataTable dt, IAsyncResult result)
        {
            if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate1))
            {
                FillDataTableDelegate1 d = (FillDataTableDelegate1)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate2))
            {
                FillDataTableDelegate2 d = (FillDataTableDelegate2)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate3))
            {
                FillDataTableDelegate3 d = (FillDataTableDelegate3)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate4))
            {
                FillDataTableDelegate4 d = (FillDataTableDelegate4)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate5))
            {
                FillDataTableDelegate5 d = (FillDataTableDelegate5)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate6))
            {
                FillDataTableDelegate6 d = (FillDataTableDelegate6)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate7))
            {
                FillDataTableDelegate7 d = (FillDataTableDelegate7)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate8))
            {
                FillDataTableDelegate8 d = (FillDataTableDelegate8)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
            else //if ((((AsyncResult)result).AsyncDelegate).GetType() == typeof(FillDataTableDelegate9))
            {
                FillDataTableDelegate9 d = (FillDataTableDelegate9)((AsyncResult)result).AsyncDelegate;
                d.EndInvoke(ref dt, result);
            }
        }

        #endregion FillDataTable

        #region SqlBulkCopy
        /// <summary>
        /// 批量插入数据
        /// </summary>
        /// <param name="connectionString">数据库链接串</param>
        /// <param name="ds">数据集,其中第一个表名要和数据库表名相同，列名和库表列名一致</param>
        /// <returns></returns>
        public static int SqlBulkCopyData(string connectionString, DataSet ds)
        {
            int effectRowCount = -1;
            if(ds!=null
                &&ds.Tables.Count>0
                &&!string.IsNullOrEmpty(ds.Tables[0].TableName)
                &&ds.Tables[0].Rows.Count>0)
            try
            {
                SqlConnection conn = new SqlConnection(connectionString);
                SqlBulkCopy sqlbc = new SqlBulkCopy(conn);
                sqlbc.BulkCopyTimeout = 300;
                conn.Open();

                sqlbc.DestinationTableName = ds.Tables[0].TableName;
                foreach (DataColumn col in ds.Tables[0].Columns)
                {
                    sqlbc.ColumnMappings.Add(col.ColumnName, col.ColumnName);
                }
                sqlbc.WriteToServer(ds.Tables[0]);
                sqlbc.Close();

                conn.Close();
                conn.Dispose();
                effectRowCount = ds.Tables[0].Rows.Count;
            }
            catch (SqlException ex)
            {
                throw ex;
            }
            return effectRowCount;
        }
        #endregion
    }

    /// <summary>
    /// SqlHelperParameterCache provides functions to leverage a static cache of procedure parameters, and the
    /// ability to discover parameters for stored procedures at run-time.
    /// </summary>
    public sealed class SqlHelperParameterCache
    {
        #region private methods, variables, and constructors

        //Since this class provides only static methods, make the default constructor private to prevent 
        //instances from being created with "new SqlHelperParameterCache()"
        private SqlHelperParameterCache() { }

        private static Hashtable paramCache = Hashtable.Synchronized(new Hashtable());

        /// <summary>
        /// Resolve at run time the appropriate set of SqlParameters for a stored procedure
        /// </summary>
        /// <param name="connection">A valid SqlConnection object</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <param name="includeReturnValueParameter">Whether or not to include their return value parameter</param>
        /// <returns>The parameter array discovered.</returns>
        private static SqlParameter[] DiscoverSpParameterSet(SqlConnection connection, string spName, bool includeReturnValueParameter)
        {
            if (connection == null) throw new ArgumentNullException("connection");
            if (spName == null || spName.Length == 0) throw new ArgumentNullException("spName");

            SqlCommand cmd = new SqlCommand(spName, connection);
            cmd.CommandType = CommandType.StoredProcedure;

            connection.Open();
            SqlCommandBuilder.DeriveParameters(cmd);
            connection.Close();

            if (!includeReturnValueParameter)
            {
                cmd.Parameters.RemoveAt(0);
            }

            SqlParameter[] discoveredParameters = new SqlParameter[cmd.Parameters.Count];

            cmd.Parameters.CopyTo(discoveredParameters, 0);

            // Init the parameters with a DBNull value
            foreach (SqlParameter discoveredParameter in discoveredParameters)
            {
                discoveredParameter.Value = DBNull.Value;
            }
            return discoveredParameters;
        }

        /// <summary>
        /// Deep copy of cached SqlParameter array
        /// </summary>
        /// <param name="originalParameters"></param>
        /// <returns></returns>
        private static SqlParameter[] CloneParameters(SqlParameter[] originalParameters)
        {
            SqlParameter[] clonedParameters = new SqlParameter[originalParameters.Length];

            for (int i = 0, j = originalParameters.Length; i < j; i++)
            {
                clonedParameters[i] = (SqlParameter)((ICloneable)originalParameters[i]).Clone();
            }

            return clonedParameters;
        }

        #endregion private methods, variables, and constructors

        #region caching functions

        /// <summary>
        /// Add parameter array to the cache
        /// </summary>
        /// <param name="connectionString">A valid connection string for a SqlConnection</param>
        /// <param name="commandText">The stored procedure name or T-SQL command</param>
        /// <param name="commandParameters">An array of SqlParamters to be cached</param>
        public static void CacheParameterSet(string connectionString, string commandText, params SqlParameter[] commandParameters)
        {
            if (connectionString == null || connectionString.Length == 0) throw new ArgumentNullException("connectionString");
            if (commandText == null || commandText.Length == 0) throw new ArgumentNullException("commandText");

            string hashKey = connectionString + ":" + commandText;

            paramCache[hashKey] = commandParameters;
        }

        /// <summary>
        /// Retrieve a parameter array from the cache
        /// </summary>
        /// <param name="connectionString">A valid connection string for a SqlConnection</param>
        /// <param name="commandText">The stored procedure name or T-SQL command</param>
        /// <returns>An array of SqlParamters</returns>
        public static SqlParameter[] GetCachedParameterSet(string connectionString, string commandText)
        {
            if (connectionString == null || connectionString.Length == 0) throw new ArgumentNullException("connectionString");
            if (commandText == null || commandText.Length == 0) throw new ArgumentNullException("commandText");

            string hashKey = connectionString + ":" + commandText;

            SqlParameter[] cachedParameters = paramCache[hashKey] as SqlParameter[];
            if (cachedParameters == null)
            {
                return null;
            }
            else
            {
                return CloneParameters(cachedParameters);
            }
        }

        #endregion caching functions

        #region Parameter Discovery Functions

        /// <summary>
        /// Retrieves the set of SqlParameters appropriate for the stored procedure
        /// </summary>
        /// <remarks>
        /// This method will query the database for this information, and then store it in a cache for future requests.
        /// </remarks>
        /// <param name="connectionString">A valid connection string for a SqlConnection</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <returns>An array of SqlParameters</returns>
        public static SqlParameter[] GetSpParameterSet(string connectionString, string spName)
        {
            return GetSpParameterSet(connectionString, spName, false);
        }

        /// <summary>
        /// Retrieves the set of SqlParameters appropriate for the stored procedure
        /// </summary>
        /// <remarks>
        /// This method will query the database for this information, and then store it in a cache for future requests.
        /// </remarks>
        /// <param name="connectionString">A valid connection string for a SqlConnection</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <param name="includeReturnValueParameter">A bool value indicating whether the return value parameter should be included in the results</param>
        /// <returns>An array of SqlParameters</returns>
        public static SqlParameter[] GetSpParameterSet(string connectionString, string spName, bool includeReturnValueParameter)
        {
            if (connectionString == null || connectionString.Length == 0) throw new ArgumentNullException("connectionString");
            if (spName == null || spName.Length == 0) throw new ArgumentNullException("spName");

            using (SqlConnection connection = new SqlConnection(connectionString))
            {
                return GetSpParameterSetInternal(connection, spName, includeReturnValueParameter);
            }
        }

        /// <summary>
        /// Retrieves the set of SqlParameters appropriate for the stored procedure
        /// </summary>
        /// <remarks>
        /// This method will query the database for this information, and then store it in a cache for future requests.
        /// </remarks>
        /// <param name="connection">A valid SqlConnection object</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <returns>An array of SqlParameters</returns>
        internal static SqlParameter[] GetSpParameterSet(SqlConnection connection, string spName)
        {
            return GetSpParameterSet(connection, spName, false);
        }

        /// <summary>
        /// Retrieves the set of SqlParameters appropriate for the stored procedure
        /// </summary>
        /// <remarks>
        /// This method will query the database for this information, and then store it in a cache for future requests.
        /// </remarks>
        /// <param name="connection">A valid SqlConnection object</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <param name="includeReturnValueParameter">A bool value indicating whether the return value parameter should be included in the results</param>
        /// <returns>An array of SqlParameters</returns>
        internal static SqlParameter[] GetSpParameterSet(SqlConnection connection, string spName, bool includeReturnValueParameter)
        {
            if (connection == null) throw new ArgumentNullException("connection");
            using (SqlConnection clonedConnection = (SqlConnection)((ICloneable)connection).Clone())
            {
                return GetSpParameterSetInternal(clonedConnection, spName, includeReturnValueParameter);
            }
        }

        /// <summary>
        /// Retrieves the set of SqlParameters appropriate for the stored procedure
        /// </summary>
        /// <param name="connection">A valid SqlConnection object</param>
        /// <param name="spName">The name of the stored procedure</param>
        /// <param name="includeReturnValueParameter">A bool value indicating whether the return value parameter should be included in the results</param>
        /// <returns>An array of SqlParameters</returns>
        private static SqlParameter[] GetSpParameterSetInternal(SqlConnection connection, string spName, bool includeReturnValueParameter)
        {
            if (connection == null) throw new ArgumentNullException("connection");
            if (spName == null || spName.Length == 0) throw new ArgumentNullException("spName");

            string hashKey = connection.ConnectionString + ":" + spName + (includeReturnValueParameter ? ":include ReturnValue Parameter" : "");

            SqlParameter[] cachedParameters;

            cachedParameters = paramCache[hashKey] as SqlParameter[];
            if (cachedParameters == null)
            {
                SqlParameter[] spParameters = DiscoverSpParameterSet(connection, spName, includeReturnValueParameter);
                paramCache[hashKey] = spParameters;
                cachedParameters = spParameters;
            }

            return CloneParameters(cachedParameters);
        }

        #endregion Parameter Discovery Functions
    }
}
